mapper.insert:

  SELECT LAST_INSERT_ID()

insert into sys_user (user_name, password, name, 
  sex, phone, email, 
  status, update_time, create_time
  )
values (#{userName,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{sex,jdbcType=TINYINT}, #{phone,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, 
  #{status,jdbcType=TINYINT}, #{updateTime,jdbcType=TIMESTAMP}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
